mapper.select:
select m.majorId, m.majorName, d.dept_id, d.full_name from major m inner join ( select DISTINCT m.majorId from major m inner join major_dept md on m.majorId = md.majorId inner join cy_dept_info d on md.deptId = d.dept_id
md.deptId like concat(#{dept.deptId},'%')
and m.majorName like concat('%',#{majorName},'%')
and LEFT(md.deptId,6) = #{school}
and md.deptId = #{department}
order by convert( m.majorName using gbk ) collate gbk_chinese_ci limit #{start},#{rows} ) AS temp on m.majorId = temp.majorId left join major_dept md on m.majorId = md.majorId left join cy_dept_info d on md.deptId = d.dept_id

select count(distinct m.majorId) from major m inner join major_dept md on m.majorId = md.majorId inner join cy_dept_info d on md.deptId = d.dept_id
md.deptId like concat(#{dept.deptId},'%')
and m.majorName like concat('%',#{majorName},'%')
and LEFT(md.deptId,6) = #{school}
and md.deptId = #{department}
